changeSet.sql:
create table bank_account ( id SERIAL PRIMARY KEY,
created_at TIMESTAMP NOT NULL, created_by INTEGER NOT NULL, updated_at TIMESTAMP NOT NULL, updated_by INTEGER NOT NULL,
balance NUMERIC NOT NULL );

changeSet.createProcedure:
create or replace function is_receive_money_transfer_operation(INTEGER) returns boolean as
$$
select exists(
               select 1
               from "operation"
               where id = $1
                 and operation_source = 'RECEIVE_MONEY_TRANSFER'
           );
$$ language sql;
