changeSet.sql:
create table customer ( id SERIAL PRIMARY KEY,
created_at TIMESTAMP WITH TIME ZONE NOT NULL, created_by INTEGER NOT NULL, updated_at TIMESTAMP WITH TIME ZONE NOT NULL, updated_by INTEGER NOT NULL,
name TEXT NOT NULL, balance NUMERIC NOT NULL );

create table money_transfer ( id SERIAL PRIMARY KEY,
created_at TIMESTAMP WITH TIME ZONE NOT NULL, created_by INTEGER NOT NULL, updated_at TIMESTAMP WITH TIME ZONE NOT NULL, updated_by INTEGER NOT NULL,
fk_from_customer INTEGER NOT NULL REFERENCES customer (id), fk_to_customer INTEGER NOT NULL REFERENCES customer (id), amount NUMERIC NOT NULL );
